import React from "react";
import AuthApi from './AuthApi';
import Cookies from 'js-cookie';
import apple from './Images/apple-background.jpg'
import { withRouter } from "react-router-dom";


class Welcome extends React.Component {
  //const Auth = React.useContext(AuthApi)
  handleOnClick = () => {
    this.props.signout();
    this.props.history.push('/login');
  }

  render() {

    setTimeout(function(){
      var base = document.getElementById('base');
      base.classList.remove("init");
      base.classList.add("end");
      
    }, 800);

    return (
      <div id="base" className="base-welcome frame zoomout init">
        <div className="container">
          <div className="header-name">
            <h1>Welcome to Apple</h1>
          </div>
          <button onClick={this.handleOnClick} type="button" className="btn">
            Logout
          </button>
        </div>

      </div>
    );
  }

}

export default withRouter(Welcome);
