import React from "react";
import AuthApi from './AuthApi';
import Cookies from 'js-cookie';
import desktopImage from './Images/apple-background.jpg'
import mobileImage from './Images/apple-background.jpg'


function Welcome() {
  const Auth = React.useContext(AuthApi)
  const handleOnClick = () => {
    Auth.setAuth(false)
    Cookies.remove("user")
  }
  const imageUrl = window.innerWidth >= 650 ? desktopImage : mobileImage;
  return (
    <div className="base-welcome">
      <div className="container">
        <div style={{backgroundImage: `url(${imageUrl})` }}>
          <h1>Welcome to Apple</h1>
        </div>
        <button onClick={handleOnClick} type="button" className="btn">
          Logout
          </button>
      </div>

    </div>
  );

}

export default Welcome;
