class App extends Component {
  isAuthenticated = (cb) => {
    return Cookies.get("user") === "loginTrue";
  }

  authenticate = (cb) => {
    Cookies.set("user", "loginTrue");
    setTimeout(cb, 100);
  }

  signout = (cb) => {
    Cookies.remove("user");
    setTimeout(cb, 100);
  }

  render() {
    return (
      <Router>
        <Route path="/login" component={Login} authenticate={this.authenticate} />
        <Route path="/register" component={Register} authenticate={this.authenticate} />
        <PrivateRoute path="/welcome" component={Welcome} />
      </Router>
    )
  }
}

const PrivateRoute = ({ component: Component, ...rest }) => (
  <Route {...rest} render={(props) => (
    this.isAuthenticated() === true
      ? <Component {...props} />
      : <Redirect to="/login" />
  )} />
)

