import React from "react";
import AuthApi from './AuthApi';
import Cookies from 'js-cookie';
import apple from './Images/apple-background.jpg'
import { withRouter } from "react-router-dom";


class Welcome extends React.Component {

  constructor() {
    super();
    this.state = {
       zoomOutClassName: 'btn'
    }
  }

  //const Auth = React.useContext(AuthApi)
  handleOnClick = () => {
    this.props.signout();
    this.props.history.push('/login');
  }

  headerZoomOut() {
    let zoomOutClassName = this.state.zoomOutClassName;
    zoomOutClassName = zoomOutClassName + ' ' + 'end'
    this.setState({zoomOutClassName});
  }


  render() {

    setTimeout(this.headerZoomOut, 1000);

    return (
      
        
        <div className="base-welcome">
         
            <div className="container">
              <div className="header-wrap zoomout header-name">
                <h3 className={this.state.zoomOutClassName}>New Products Comming This Summer</h3>
              </div>
              <button onClick={this.handleOnClick} type="button" className="btn">
                Logout
              </button>
            </div>
         
        </div>
      
    );
  }

}

export default withRouter(Welcome);
