import React from 'react';
import './App.css';
import "./style.scss";
import Login from './login';
import Register from './register';
import Welcome from './welcome';
import AuthApi from './AuthApi';
import Cookies from 'js-cookie';


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";

function App() {
  const [auth, setAuth] = React.useState(false);

  const readCookie = () => {
    const user = Cookies.get("user");
    if (user) {
      setAuth(true);
    }
  }
    React.useEffect(() => {
      readCookie();
    }, [])

    return (
      <div className="App">
        <AuthApi.Provider value={{ auth, setAuth }}>
          <Router>
            <Routes />
          </Router>
        </AuthApi.Provider>
      </div>
    );
  }

  const Routes = () => {
    const Auth = React.useContext(AuthApi)
    return (
      <Switch>
        <ProtectedLogin path="/login" exact auth={Auth.auth} component={Login} />
        <ProtectedRegister path="/register" exact auth={Auth.auth} component={Register} />
        <ProtectedRoute path="/welcome" exact auth={Auth.auth} component={Welcome} />
        <Route exact path='/home' component={Home} />
      </Switch>
    )

  }

  const ProtectedRoute = ({ auth, component: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        render={() => auth ? (
          <Component />
        ) :
          (
            <Redirect to="/login" />
          )

        }
      />
    )
  }

  const ProtectedLogin = ({ auth, component: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        render={() => !auth ? (
          <Component />
        ) :
          (
            <Redirect to="/welcome" />
          )

        }
      />
    )
  }
  const ProtectedRegister = ({ auth, component: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        render={() => !auth ? (
          <Component />
        ) :
          (
            <Redirect to="/welcome" />
          )

        }
      />
    )
  }



  export default App;
