import React from 'react';
import './App.css';
import "./style.scss";
import Login from './login';
import Register from './register';
import Welcome from './welcome';
import AuthApi from './AuthApi';
import Cookies from 'js-cookie';


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";

function App() {
  const [auth, setAuth] = React.useState(false);

  isAthenticated = () => {
    const user = Cookies.get("user");
    if (user) {
      return true;
      // setAuth(true);
    }
    return false;
  }
  React.useEffect(() => {
    isAthenticated();
  }, [])

  return (
    <div className="App">
      <AuthApi.Provider value={{ auth, setAuth }}>
        <Router>
          <Routes />
        </Router>
      </AuthApi.Provider>
    </div>
  );
}

const Routes = () => {
  const Auth = React.useContext(AuthApi)
  return (
    <Switch>
      <ProtectedLogin path="/login" exact component={Login} />
      <ProtectedRegister path="/register" exact component={Register} />
      <ProtectedRoute path="/"  component={Welcome} />
    </Switch>
  )

}

const ProtectedRoute = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={() => this.isAthenticated() ? (
        <Component />
      ) :
        (
          <Redirect to="/login" />
        )

      }
    />
  )
}

const ProtectedLogin = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={() => !this.isAthenticated() ? (
        <Component />
      ) :
        (
          <Redirect to="/welcome" />
        )

      }
    />
  )
}
const ProtectedRegister = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={() => !this.isAthenticated() ? (
        <Component />
      ) :
        (
          <Redirect to="/welcome" />
        )

      }
    />
  )
}



export default App;
