import React, { Component } from "react";
import { Link, Redirect, withRouter } from "react-router-dom";
import { Formik, Form, Field } from "formik";
import * as Yup from 'yup';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';

class Login extends React.Component {

  constructor(props) {
    super(props);
  }

  handleOnClick = () => {
    this.props.authenticate(() => {
      console.log('cookie is in from the invocation');
      this.props.history.push('/welcome')
    });

  }

  render() {


    return (
      this.props.isAuthenticated() ?
        <Redirect to="/welcome" />
        :

        <div className="base-container">
          <div className="content">
            <div className="form" >
              <div className="header">Sing-In</div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input type="email" name="email" placeholder="email" />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" name="password" placeholder="password" />
              </div>
              <div className="footer">
                <Button variant="dark" className="mr-2" onClick={this.handleOnClick}>Sing-In</Button>
              </div>
            </div>
            <div className="link-register">
              <div>Not registered ? <Link to="register">Sign-up</Link></div>
            </div>
          </div>
        </div>
    );
  }

}

<div className="container">
  <div className="row mt-5">
    <div className="col-lg-4 mb-4 grid-margin">
      <div className="card h-100">
        <h4 className="card-header">Card Title</h4>
        <div className="card-body">
          <p className="card-text">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sapiente esse necessitatibus neque.</p>
        </div>
        <div className="card-footer">
          <Button variant="btn btn-primary">Learn More</Button>
        </div>
      </div>
    </div>

  </div>

</div>

export default withRouter(Login);
